import NextAuth from 'next-auth'
import Providers from 'next-auth/providers'
import bcrypt from 'bcrypt'
import { MongoClient } from 'mongodb'

const SALT_ROUNDS = 12

const uri = "mongodb://mongo:27017";

const client = new MongoClient(uri);

const options = {
  // Configure one or more authentication providers
  providers: [
    Providers.Credentials({
      // The name to display on the sign in form (e.g. 'Sign in with...')
      name: 'Credentials',
      // The credentials is used to generate a suitable form on the sign in page.
      // You can specify whatever fields you are expecting to be submitted.
      // e.g. domain, username, password, 2FA token, etc.
      credentials: {
        username: { label: "Username", type: "text", placeholder: "jsmith" },
        password: {  label: "Password", type: "password" }
      },
      authorize: async (credentials) => {
        // Add logic here to look up the user from the credentials supplied
        console.log(credentials)
        const user = { id: 1, name: 'J Smith', email: 'jsmith@example.com' }

        await client.connect()
        const database = client.db("accounts")
        const collection = database.collection("users")

        const query = { username: credentials.username }

        let found = await collection.findOne(query)
        console.log(found)
        if (!found) return Promise.reject(new Error("Sorry, we could not find an account with that username!"))

        let compare = await bcrypt.compare(credentials.password, found.hash)
        console.log(compare)
        if (!compare) return Promise.reject(new Error("Sorry, that password does not seem to match!"))

        return Promise.resolve(found)
      }
    })
  ]
}

export default (req, res) => NextAuth(req, res, options)
