import styled from 'styled-components'

let Container = styled.div`
  font-family: 'Spectral';
  padding: 1rem;
  box-sizing: border-box;
  background-color: white;
  margin: 2rem 0;
  box-shadow: 0px 20px 20px rgba(0, 0, 0, 0.03);
`

let Attribute = styled.div`
  display: inline;
  border-radius: 10px;
  background: black;
  color: white;
  font-family: 'Work Sans';
  font-weight: 100;
  padding: 1rem 0;
`

const Post = ({post}) => <Container>
  <div>
    {post.emotions_description}
  </div>
  <div>
    {post.event_description}
  </div>
{Object.entries(post.emotions).sort(([_, rank]) => rank).map(([emotion, rank]) => <Attribute>{emotion} {rank}</Attribute>)}
</Container>

export default Post
