import bcrypt from 'bcrypt'
import { MongoClient } from 'mongodb'
import css from 'next-auth/dist/css'

console.log(css)

const uri = "mongodb://mongo:27017";

const SALT_ROUNDS = 12

const client = new MongoClient(uri);

const handleGet = async (res) => {
  res.statusCode = 200
  res.setHeader('Content-Type','text/html')
  res.send(`
    <!DOCTYPE html>
      <head>
        <style type="text/css">${css()}</style>
        <meta name="viewport" content="width=device-width, initial-scale=1">
      </head>
      <body>
        <div class="page">
          <div class='signin'>
            <div class='provider'>
              <form method='post' action='/api/auth/register'>
                <label>
                  Username
                  <input name='username' type='text'/>
                </label>
                <label>
                  Password
                  <input name='password' type='password'/>
                </label>
                <button type='submit'>Register</button>
              </form>
            </div>
          </div>
        </div>
      </body>
    </html>
  `)
}

const handlePost = async (username, password, method, res) => {
  await client.connect()
  const database = client.db("accounts")
  const collection = database.collection("users")

  let hash = await bcrypt.hash(password, SALT_ROUNDS)

  const user = { username, hash }

  try {
    const result = await collection.insertOne(user)
    console.log(result)
    res.statusCode = 200
    res.setHeader('Content-Type', 'application/json')
    res.end(JSON.stringify(result))
  } catch (e) {
    console.log(e)
    res.statusCode = 409
    res.setHeader('Content-Type', 'application/json')
    res.end(JSON.stringify(e))
  }

}

export default async ({body: {username, password}, method}, res) => {
  if (method !== "POST" && method !== "GET") {
    res.statusCode = 405
    res.setHeader('Content-Type', 'application/json')
    res.end(JSON.stringify({message: "Register only accepts GET or POST requests"}))
  }
  else if (method === "GET") await handleGet(res)
  else await handlePost(username, password, method, res)
}
